import React, { ChangeEvent, useEffect, useState } from "react";
import { Datum } from '../models/Stock';
import Highcharts from 'highcharts/highstock';
import HighchartsReact from 'highcharts-react-official';
import { useRecoilState, useRecoilValue } from "recoil";
import { stocksAtom, stocksSelector } from "../states/StockState/StockList";
import axios from "axios";
import Alert from 'react-popup-alert';

const Report: React.FC = () => {
    const [, setStocks] = useRecoilState(stocksAtom);
    const stocks = useRecoilValue(stocksSelector);
    const [searchSymbol, setSearchSymbol] = useState('');
    const [alert, setAlert] = React.useState({
        type: 'error',
        text: 'No Data Found',
        show: false
      })

    const getData = (param: string) => {
        param=param.toUpperCase();
        if(param)
        {
        axios.get('http://api.marketstack.com/v1/eod?access_key=3dc53bdf0a3a0a47b62270dae3b28b5b&symbols='+param)
            .then((response) => {
                if (response) {
                    console.log(response.data);
                    const result = response.data;
                    if (result.data && result.data.length) {
                        setStocks(result.data as Datum[]);
                    }
                    else{
                        onShowAlert();
                    }
                }
            });
        }
        else{
            onShowAlert();
        }
    }

    const onChangeSearchSymbol = (e: ChangeEvent<HTMLInputElement>) => {
        const searchSymbol = e.target.value;
        setSearchSymbol(searchSymbol);       
      };

      const findBySymbol = () => {
        getData(searchSymbol);          
      };
      
      function onCloseAlert() {
        setAlert({
          type: '',
          text: '',
          show: false
        })
      }
    
      function onShowAlert() {
        setAlert({
          type: 'Error',
          text: 'No Data Found',
          show: true
        })
      }

    return (
        <>
    <div className="col-md-8">
        <div className="input-group mb-3">
          <input
            type="text"
            className="form-control"
            placeholder="Search by symbol"
            value={searchSymbol}
            onChange={onChangeSearchSymbol}
          />
          <div className="input-group-append">
            <button
              className="btn btn-outline-secondary"
              type="button"
              onClick={findBySymbol}
            >
              Search
            </button>
          </div>
        </div>
      </div>
            <div className="list row">                
                <div className='App'>
                    <HighchartsReact highcharts={Highcharts} options={stocks} />
                </div>
            </div>
        </>
    );
};

export default Report;
