import { useState } from "react";
import { atom, selector } from "recoil";
import { Datum } from "../../models/Stock";

const stocksAtom = atom({
    key: 'stocks',
    default: [] as Datum[]
});

const stocksSelector = selector({
    key: 'stocksChart',
    get: ({ get }) => {
        const stocks = get(stocksAtom);

        let volume: number[] = [];
        let open: number[] = [];
        let close: number[] = [];
        let high: number[] = [];
        let low: number[] = [];

        stocks.forEach(stock => {
            volume.push(stock.volume);
            open.push(stock.open);
            close.push(stock.close);
            high.push(stock.high);
            low.push(stock.low);

        });

        return {
            title: {
                
                    text: '<span style="font-size: 26px; font-style: oblique">Display Stock Data</span> '
                  
                // text: 'Display Stock Data',
                // style: {
                //     color: 'Black',
                                       
                // }
            },
            chart: {
                backgroundColor: '#FCFFC5'                
            },
            xAxis: {
                categories: volume,
            },
            series: [{
                name: 'open',
                data: open,
            }, {
                name: 'low',
                data: low
            }, {
                name: 'high',
                data: high
            },
            {
                name: 'close',
                data: close
            }],
           
        }

    }
});



export { stocksAtom, stocksSelector };
